import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

class Clock extends React.Component{
  constructor(props){
    super(props)
    this.state = {date: new Date()}
  }

  componentDidMount(){
    this.timerID = setInterval(
      () => this.tick(), 
      1000
      )
  }

  componentWillUnmount(){
    clearInterval(this.timerID);
  }

  tick(){
    this.setState({
      date: new Date()
    });
  }

  render(){
    return(
      <h4>{this.state.date.toLocaleTimeString()}</h4>
    );
  }
}

class Login extends React.Component{
  constructor(props){
    super(props)
    this.state = {name: '', password: ''}
  }

  handleName(event){
    this.setState({name: event.target.value});
  }

  handlePass(event){
    this.setState({password: event.target.pass});
  }


  render(){
    return(
      <div>
        <form>
          <label for="uname">Username:</label>
          <input type="text" id="uname" value={this.state.name} name="uname" onChange={this.handleName}/>
        </form>
        <form>
          <label for="pass">Password:</label>
          <input type="password" id="pass" value={this.state.password} name="pass" onChange={this.handlePass}/>
        </form>
        <form>
          <input type="submit" value="Submit"/>
        </form>
      </div>
    );
  }
}



ReactDOM.render(
  <div>
    <h4>Welcome</h4>
    <Clock/>
    <Login/>

  </div>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
